mapper.select:
select t.id, t.site_id, t.column_id, t.title, t.subtitle, t.excerpt, t.title_font_family, t.title_color, t.title_font_weight, t.title_font_size, t.source, t.author, t.tags, t.release_date, t.offline_date, t.order_no, t.view_count, cc.name as column_name, sf.path as cover_url
, ${appendField}
from cms_article t left join cms_column cc on cc.id = t.column_id left join sys_user su_edit_user on su_edit_user.id = t.edit_user left join sys_file sf on sf.parent_id = t.id and sf.type = '${@com.easy.admin.cms.common.type.CmsFileType@ARTICLE_COVER.code}'
${ew.sqlSegment}

select cc.id, cc.site_id, cc.name, cc.slug from cms_article t
    left join cms_column cc on cc.id = t.column_id
where t.id = #{id}
