mapper.select:
select t.id, t.site_id, t.parent_id, t.name, t.slug, t.type, t.is_release, t.status, su_edit_user.nickname as edit_user, t.edit_date from cms_column t left join sys_user su_edit_user on su_edit_user.id = t.edit_user
${ew.sqlSegment}
order by t.order_no

select ifnull(max(order_no), 0)
from cms_column t
where t.parent_id = #{parentId}

update.foreach:
update cms_column

    parent_id = #{item.parentId},
    order_no = #{item.orderNo}

where id = #{item.id}

mapper.update:
update sys_import_excel_temporary
set verification_results = concat(ifnull(verification_results, ''), '业务中已存在（', field3, '）数据;'),
    verification_status  = #{status}
where id in (
    select id from (
       select siet.id
       from cms_column cc
                left join sys_import_excel_temporary siet on siet.field1 = cc.site_id and siet.field3 = cc.slug
       where siet.template_id = #{templateId} and siet.field3 is not null
   ) t
);

mapper.select:
select t.id, t.parent_id, t.site_id, t.name, t.slug, t.type, t.is_release, t.description,
t.remarks, t.order_no, t.status
from cms_column t
where t.status = #{status}

    and t.site_id = #{siteId}
