mapper.select:
select t.id, t.parent_id, t.name, t.domain_name, t.deployment_path, t.theme, t.status, t.order_no, su_edit_user.nickname as edit_user, t.edit_date from cms_site t left join sys_user su_edit_user on su_edit_user.id = t.edit_user
${ew.sqlSegment}
order by t.order_no

select t.id, t.parent_id, t.name, t.keyword, t.description, t.domain_name, t.deployment_path, t.theme, t.status, t.order_no, t.tenant_id, t.version
from cms_site t
where t.id = #{id}

select ifnull(max(order_no), 0)
from cms_site t
where t.parent_id = #{parentId}

update.foreach:
update cms_site

    parent_id = #{item.parentId},
    order_no = #{item.orderNo}

where id = #{item.id}
