mapper.select:
select t.id, t.parent_id, t.name, t.simple_name, t.code, t.type_code, dt.name as type_name, t.status, t.order_no, su.nickname as edit_user, t.edit_date from sys_dept t left join sys_dept_type dt on dt.code = t.type_code left join sys_user su on su.id = t.edit_user
${ew.sqlSegment}
order by t.order_no

select ifnull(max(order_no), 0)
from sys_dept t
where t.parent_id = #{parentId}

select count(1) from sys_dept t
left join sys_dept_type dt on dt.code = t.type_code

    ${ew.sqlSegment}

select t.id as value, t.name as label
from sys_dept t
where t.type_code = #{typeCode}

update.foreach:
update sys_dept

    parent_id = #{item.parentId},
    order_no = #{item.orderNo}

where id = #{item.id}
