mapper.select:
select t.id, t.parent_id, t.name, t.code, t.status, t.order_no, t.remarks, t.tenant_id, t.version, t.edit_date, su.nickname as edit_user from sys_dept_type t left join sys_user su on su.id = t.edit_user
${ew.sqlSegment}
order by t.order_no

select t.id, t.parent_id, t.code, t.name, t.order_no, t.status, t.remarks, t.tenant_id, t.version
from sys_dept_type t
where t.id= #{id}

select ifnull(max(order_no), 0) from sys_dept_type t where

    t.parent_id = #{parentId}


    t.parent_id is null

update.foreach:
update sys_dept_type

    parent_id = #{item.parentId},
    order_no = #{item.orderNo}

