mapper.update:
update sys_user
set last_login_date = #{lastLogin}
where id = #{id}

mapper.select:
select t.id, t.dept_id, t.post_id, t.nickname, t.username, t.sex, t.email, t.phone_number, t.birthday, t.avatar, t.status, t.source, t.last_login_date, t.tenant_id, t.create_user, t.create_date, t.edit_user, t.edit_date from sys_user t left join sys_user_role sur on sur.user_id = t.id left join sys_role sr on sr.id = sur.role_id
${ew.sqlSegment}
group by t.id, t.dept_id, t.post_id, t.nickname, t.username, t.sex, t.email, t.phone_number, t.birthday, t.avatar, t.status, t.source, t.last_login_date, t.tenant_id, t.create_user, t.create_date, t.edit_user, t.edit_date

select t.id, t.dept_id, t.post_id, t.nickname, t.username, t.sex, t.email, t.phone_number, t.birthday, t.avatar, t.version, t.status, t.source, t.tenant_id
from sys_user t
where t.id = #{id}

select t.id, t.dept_id, t.post_id, t.nickname, t.username, t.password, t.salt, t.sex, t.email, t.phone_number,
       t.birthday, t.avatar, t.status, t.source, t.last_login_date, t.tenant_id
from sys_user t where t.username = #{account} or t.phone_number = #{account} or t.email = #{account}
